import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import './Dashboard.css';

const Dashboard = ({ role }) => {
  const [userData, setUserData] = useState(null);
  const [applications, setApplications] = useState([]);
  const [applicants, setApplicants] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const navigate = useNavigate();

  useEffect(() => {
    const fetchData = async () => {
      try {
        const token = localStorage.getItem('token');
        if (!token) {
          navigate('/login');
          return;
        }

        // Fetch user data
        const userResponse = await axios.get('http://localhost:5000/api/auth/me', {
          headers: { Authorization: `Bearer ${token}` }
        });
        setUserData(userResponse.data.user);

        if (role === 'jobSeeker') {
          // Fetch job seeker's applications
          const appsResponse = await axios.get('http://localhost:5000/api/applications/my', {
            headers: { Authorization: `Bearer ${token}` }
          });
          setApplications(appsResponse.data?.applications || []);
        } else if (role === 'company') {
          // Fetch job applicants for company's jobs
          const seekersResponse = await axios.get('http://localhost:5000/api/applications/company', {
            headers: { Authorization: `Bearer ${token}` }
          });
          setApplicants(seekersResponse.data?.applications || []);
        }
      } catch (error) {
        console.error('Dashboard error:', error);
        setError('Failed to load dashboard data. Please try again.');
        if (error.response?.status === 401) {
          localStorage.removeItem('token');
          localStorage.removeItem('role');
          navigate('/login');
        }
      } finally {
        setLoading(false);
      }
    };

    fetchData();
  }, [role, navigate]);

  const formatStatus = (status) => {
    if (!status) return 'Pending';
    return status.charAt(0).toUpperCase() + status.slice(1);
  };

  const handleStatusUpdate = async (applicationId, newStatus) => {
    try {
      const token = localStorage.getItem('token');
      await axios.put(
        `http://localhost:5000/api/applications/${applicationId}/status`,
        { status: newStatus },
        { headers: { Authorization: `Bearer ${token}` } }
      );
      // Update local state
      if (role === 'company') {
        setApplicants(applicants.map(app => 
          app._id === applicationId ? { ...app, status: newStatus } : app
        ));
      } else {
        setApplications(applications.map(app => 
          app._id === applicationId ? { ...app, status: newStatus } : app
        ));
      }
    } catch (error) {
      console.error('Error updating status:', error);
    }
  };

  const handleDownloadResume = async (resumePath, applicantName) => {
    try {
      const token = localStorage.getItem('token');
      const response = await axios.get(
        `http://localhost:5000${resumePath}`,
        {
          headers: { Authorization: `Bearer ${token}` },
          responseType: 'blob'
        }
      );
      
      const url = window.URL.createObjectURL(new Blob([response.data]));
      const link = document.createElement('a');
      link.href = url;
      link.setAttribute('download', `resume-${applicantName || 'applicant'}.pdf`);
      document.body.appendChild(link);
      link.click();
      link.remove();
    } catch (error) {
      console.error('Error downloading resume:', error);
      alert('Failed to download resume. Please try again.');
    }
  };

  if (loading) return (
    <div className="loading-container">
      <div className="spinner"></div>
      <p>Loading dashboard...</p>
    </div>
  );
  
  if (error) return (
    <div className="error-container">
      <p>{error}</p>
      <button onClick={() => window.location.reload()} className="retry-btn">
        Retry
      </button>
    </div>
  );

  return (
    <div className="dashboard-container">
      <div className="dashboard-header">
        <h2>Welcome, {userData?.name || (role === 'company' ? 'Company' : 'Job Seeker')}</h2>
        <button 
          onClick={() => navigate('/')} 
          className="home-btn"
        >
          Back to Home
        </button>
      </div>
      
      {role === 'jobSeeker' ? (
        <div className="job-seeker-dashboard">
          {/* ... (job seeker dashboard content remains the same) ... */}
        </div>
      ) : (
        <div className="company-dashboard">
          <div className="applicants-section">
            <div className="section-header">
              <h3>Job Applicants</h3>
              <button 
                onClick={() => navigate('/post-job')}
                className="post-job-btn"
              >
                Post New Job
              </button>
            </div>
            
            {applicants.length > 0 ? (
              <div className="applicants-list">
                {applicants.map(applicant => (
                  <div key={applicant._id} className="applicant-card">
                    {/* ... (other applicant info remains the same) ... */}
                    
                    <div className="applicant-documents">
                      <div className="document-section">
                        <h5>Resume/CV:</h5>
                        {applicant.resume ? (
                          <button
                            onClick={() => handleDownloadResume(applicant.resume, applicant.user?.name)}
                            className="document-link"
                          >
                            <i className="fas fa-file-pdf"></i> Download Resume
                          </button>
                        ) : (
                          <p className="no-document">No resume uploaded</p>
                        )}
                      </div>

                      {/* ... (cover letter section remains the same) ... */}
                    </div>
                  </div>
                ))}
              </div>
            ) : (
              <div className="no-applicants">
                <p>No applicants yet for your jobs.</p>
                <button 
                  onClick={() => navigate('/post-job')}
                  className="post-job-btn"
                >
                  Post a Job to Get Applicants
                </button>
              </div>
            )}
          </div>
        </div>
      )}
    </div>
  );
};

export default Dashboard;
