import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Route, Routes, Navigate } from 'react-router-dom';
import axios from 'axios';

// Import components
import Login from './components/Login';
import Signup from './components/Signup';
import JobList from './components/JobList';
import JobPostForm from './components/JobPostForm';
import Dashboard from './components/Dashboard';
import Navbar from './components/Navbar';

// App Component
const App = () => {
  const [authToken, setAuthToken] = useState(localStorage.getItem('token') || '');
  const [role, setRole] = useState(localStorage.getItem('role') || '');

  // Check if the user is logged in
  useEffect(() => {
    if (authToken) {
      // Set role from local storage
      setRole(localStorage.getItem('role'));
    }
  }, [authToken]);

  // Handle Logout
  const logout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('role');
    setAuthToken('');
    setRole('');
  };

  return (
    <Router>
      <div>
        <Navbar logout={logout} role={role} />
        <Routes>
          <Route path="/login" element={authToken ? <Navigate to="/" /> : <Login setAuthToken={setAuthToken} setRole={setRole} />} />
          <Route path="/signup" element={authToken ? <Navigate to="/" /> : <Signup setAuthToken={setAuthToken} setRole={setRole} />} />
          <Route path="/jobs" element={<JobList />} />
          <Route path="/post-job" element={role === 'company' ? <JobPostForm authToken={authToken} /> : <Navigate to="/" />} />
          <Route path="/dashboard" element={authToken ? <Dashboard role={role} /> : <Navigate to="/login" />} />
          <Route path="/" element={<JobList />} />
        </Routes>
      </div>
    </Router>
  );
};

export default App;
