import React, { useState } from 'react'; // Import useState from React
import axios from 'axios';

const JobPostForm = () => {
  const [title, setTitle] = useState('');
  const [company, setCompany] = useState('');
  const [salary, setSalary] = useState('');
  const [location, setLocation] = useState('');
  const [description, setDescription] = useState('');
  const [error, setError] = useState('');
  const [success, setSuccess] = useState(false);

  // Fetch the authToken from localStorage
  const authToken = localStorage.getItem('authToken'); // Make sure the token is stored during login

  const handlePostJob = async (e) => {
    e.preventDefault();

    // Check if token exists in localStorage before proceeding
    if (!authToken) {
      setError('User not authenticated! Please log in.');
      return;
    }

    try {
      // Make POST request to the backend to post a new job
      const response = await axios.post(
        'http://localhost:5000/api/jobs',  // API endpoint for posting a job
        { title, company, salary, location, description },
        {
          headers: { Authorization: `Bearer ${authToken}` }, // Sending the JWT token in the Authorization header
        }
      );

      if (response.status === 201) {  // If successful, show success message
        setSuccess(true);
        alert('Job posted successfully!');
        setTitle('');
        setCompany('');
        setSalary('');
        setLocation('');
        setDescription('');
      }
    } catch (error) {
      setError('Error posting the job. Please try again.');
      console.error(error);
      if (error.response) {
        console.log(error.response.data);  // Display the backend error message
      }
    }
  };

  return (
    <div>
      <h2>Post a Job</h2>
      {error && <p style={{ color: 'red' }}>{error}</p>}
      {success && <p style={{ color: 'green' }}>Job posted successfully!</p>}
      <form onSubmit={handlePostJob}>
        <input
          type="text"
          placeholder="Job Title"
          value={title}
          onChange={(e) => setTitle(e.target.value)}
          required
        />
        <input
          type="text"
          placeholder="Company"
          value={company}
          onChange={(e) => setCompany(e.target.value)}
          required
        />
        <input
          type="text"
          placeholder="Salary"
          value={salary}
          onChange={(e) => setSalary(e.target.value)}
          required
        />
        <input
          type="text"
          placeholder="Location"
          value={location}
          onChange={(e) => setLocation(e.target.value)}
          required
        />
        <textarea
          placeholder="Job Description"
          value={description}
          onChange={(e) => setDescription(e.target.value)}
          required
        />
        <button type="submit">Post Job</button>
      </form>
    </div>
  );
};

export default JobPostForm;
