import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';

const Dashboard = ({ role }) => {
  const [jobs, setJobs] = useState([]);
  const [applications, setApplications] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    const fetchData = async () => {
      try {
        const token = localStorage.getItem('token');
        if (!token) return;

        setLoading(true);
        
        if (role === 'jobSeeker') {
          const jobsResponse = await axios.get('http://localhost:5000/api/jobs', {
            headers: { Authorization: `Bearer ${token}` }
          });
          setJobs(jobsResponse.data.jobs);
        }
        
        if (role === 'company') {
          const appsResponse = await axios.get(
            'http://localhost:5000/api/applications/company', 
            { headers: { Authorization: `Bearer ${token}` } }
          );
          setApplications(appsResponse.data.applications);
        }
      } catch (err) {
        setError(err.response?.data?.message || 'Failed to fetch data');
      } finally {
        setLoading(false);
      }
    };

    fetchData();
  }, [role]);

  const updateApplicationStatus = async (appId, status) => {
    try {
      const token = localStorage.getItem('token');
      await axios.put(
        `http://localhost:5000/api/applications/${appId}/status`,
        { status },
        { headers: { Authorization: `Bearer ${token}` } }
      );
      
      setApplications(applications.map(app => 
        app._id === appId ? { ...app, status } : app
      ));
    } catch (err) {
      setError('Failed to update application status');
    }
  };

  if (loading) return <div className="loading">Loading...</div>;
  if (error) return <div className="error">{error}</div>;

  return (
    <div className="dashboard">
      <h2>{role === 'company' ? 'Company Dashboard' : 'Job Seeker Dashboard'}</h2>
      
      {role === 'company' ? (
        <div className="company-view">
          <h3>Job Applications</h3>
          {applications.length > 0 ? (
            <div className="applications-list">
              {applications.map(app => (
                <div key={app._id} className="application-card">
                  <h4>Application for: {app.job.title}</h4>
                  <p>Applicant: {app.user.name} ({app.user.email})</p>
                  <p>Cover Letter: {app.coverLetter}</p>
                  <a 
                    href={`http://localhost:5000${app.resume}`} 
                    target="_blank" 
                    rel="noopener noreferrer"
                    className="resume-link"
                  >
                    View Resume
                  </a>
                  <div className="status-actions">
                    <span>Status: {app.status}</span>
                    <select
                      value={app.status}
                      onChange={(e) => updateApplicationStatus(app._id, e.target.value)}
                    >
                      <option value="pending">Pending</option>
                      <option value="reviewed">Reviewed</option>
                      <option value="rejected">Rejected</option>
                      <option value="accepted">Accepted</option>
                    </select>
                  </div>
                </div>
              ))}
            </div>
          ) : (
            <p>No applications yet.</p>
          )}
        </div>
      ) : (
        <div className="jobseeker-view">
          <h3>Available Jobs</h3>
          {jobs.length > 0 ? (
            <ul className="jobs-list">
              {jobs.map((job) => (
                <li key={job._id} className="job-card">
                  <h4>{job.title}</h4>
                  <p>Company: {job.company}</p>
                  <p>Location: {job.location}</p>
                  <p>Salary: {job.salary}</p>
                  <Link 
                    to={`/apply/${job._id}`} 
                    className="apply-button"
                  >
                    Apply Now
                  </Link>
                </li>
              ))}
            </ul>
          ) : (
            <p>No jobs available.</p>
          )}
        </div>
      )}
    </div>
  );
};

export default Dashboard;
