import React, { useEffect, useState } from 'react';
import axios from 'axios';

const Dashboard = ({ role }) => {
  const [jobs, setJobs] = useState([]);

  useEffect(() => {
    const fetchJobs = async () => {
      try {
        const token = localStorage.getItem('token');
        if (token) {
          const response = await axios.get('http://localhost:5000/api/jobs', {
            headers: {
              Authorization: `Bearer ${token}`,
            },
          });
          setJobs(response.data.jobs);
        }
      } catch (error) {
        console.error('Failed to fetch jobs:', error.response?.data?.message || 'Server error');
      }
    };

    fetchJobs();
  }, []);

  return (
    <div>
      <h2>{role === 'company' ? 'Company Dashboard' : 'Job Seeker Dashboard'}</h2>
      {role === 'company' ? (
        <p>Here you can manage your job postings.</p>
      ) : (
        <div>
          <h3>Available Jobs</h3>
          {jobs.length > 0 ? (
            <ul>
              {jobs.map((job) => (
                <li key={job._id}>
                  <h4>{job.title}</h4>
                  <p>{job.description}</p>
                  <p>Posted by: {job.postedBy.name}</p>
                </li>
              ))}
            </ul>
          ) : (
            <p>No jobs available.</p>
          )}
        </div>
      )}
    </div>
  );
};

export default Dashboard;
