import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import Login from './components/Login';
import Signup from './components/Signup';
import Dashboard from './components/Dashboard';
import Homepage from './components/Homepage';
import Navbar from './components/Navbar';
import PostJobPage from './components/PostJobPage';
import ApplyJob from './components/ApplyJob';
import './App.css';

const App = () => {
  const [authToken, setAuthToken] = useState(localStorage.getItem('token') || '');
  const [role, setRole] = useState(localStorage.getItem('role') || '');

  useEffect(() => {
    if (authToken) {
      setRole(localStorage.getItem('role'));
    }
  }, [authToken]);

  const logout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('role');
    setAuthToken('');
    setRole('');
  };

  return (
    <Router>
      <div className="app">
        <Navbar logout={logout} role={role} />
        <div className="content">
          <Routes>
            <Route path="/" element={<Homepage />} />
            <Route 
              path="/login" 
              element={authToken ? <Navigate to="/dashboard" /> : 
                <Login setAuthToken={setAuthToken} setRole={setRole} />} 
            />
            <Route 
              path="/signup" 
              element={authToken ? <Navigate to="/dashboard" /> : 
                <Signup setAuthToken={setAuthToken} setRole={setRole} />} 
            />
            <Route 
              path="/post-job" 
              element={role === 'company' ? 
                <PostJobPage authToken={authToken} /> : <Navigate to="/" />} 
            />
            <Route 
              path="/dashboard" 
              element={authToken ? <Dashboard role={role} /> : <Navigate to="/login" />} 
            />
            <Route 
              path="/apply/:jobId" 
              element={authToken && role === 'jobSeeker' ? 
                <ApplyJob /> : <Navigate to="/login" />} 
            />

          </Routes>
        </div>
      </div>
    </Router>
  );
};

export default App;
