import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import './NavBar.css';

function NavBar() {
  const [isMobile, setIsMobile] = useState(false); // To toggle the mobile menu visibility

  const toggleMenu = () => {
    setIsMobile(!isMobile);
  };

  const handleLinkClick = () => {
    // Close the menu when a link is clicked
    if (isMobile) {
      setIsMobile(false);
    }
  };

  return (
    <nav className="navbar">
      <div className="navbar-container">
        {/* Logo/Brand */}
        <div className="navbar-brand">
          <h2>JobBoard</h2>
        </div>

        {/* Navigation Links */}
        <div className={`navbar-links ${isMobile ? 'active' : ''}`}>
          <Link to="/" className="navbar-link" onClick={handleLinkClick}>
            Home
          </Link>
          <Link to="/post-job" className="navbar-link" onClick={handleLinkClick}>
            Post a Job
          </Link>
        </div>

        {/* Hamburger Icon */}
        <button className="navbar-hamburger" onClick={toggleMenu}>
          <span className="navbar-hamburger-line"></span>
          <span className="navbar-hamburger-line"></span>
          <span className="navbar-hamburger-line"></span>
        </button>
      </div>
    </nav>
  );
}

export default NavBar;
