import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';
import './Dashboard.css';

const Dashboard = ({ role }) => {
  const [appliedJobs, setAppliedJobs] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    const fetchData = async () => {
      try {
        const token = localStorage.getItem('token');
        if (!token) return;

        setLoading(true);

        // Fetch applied jobs for job seekers
        if (role === 'jobSeeker') {
          const response = await axios.get('http://localhost:5000/api/applications/my-applications', {
            headers: { Authorization: `Bearer ${token}` }
          });
          setAppliedJobs(response.data.applications);
        } 
      } catch (err) {
        setError(err.response?.data?.message || 'Failed to fetch data');
      } finally {
        setLoading(false);
      }
    };

    fetchData();
  }, [role]);

  if (loading) return <div className="loading">Loading...</div>;
  if (error) return <div className="error">{error}</div>;

  // Job Seeker Dashboard
  if (role === 'jobSeeker') {
    return (
      <div className="dashboard">
        <h2>Your Applied Jobs</h2>
        {appliedJobs.length > 0 ? (
          <div className="applied-jobs-container">
            {appliedJobs.map((application) => (
              <div key={application._id} className="applied-job-card">
                <div className="job-info">
                  <h3>{application.job.title}</h3>
                  <p>{application.job.company}</p>
                  <p>{application.job.location}</p>
                  <p>{application.job.salary}</p>
                </div>
                <div className="application-info">
                  <p>Status: {application.status}</p>
                  <a href={`http://localhost:5000${application.resume}`} target="_blank" rel="noopener noreferrer">
                    View Resume
                  </a>
                </div>
              </div>
            ))}
          </div>
        ) : (
          <div>No applications yet. <Link to="/">Browse available jobs</Link></div>
        )}
      </div>
    );
  }

  return (
    <div className="dashboard">
      <h2>Company Dashboard</h2>
      <div>{/* Company-specific content */}</div>
    </div>
  );
};

export default Dashboard;
