import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useParams, useNavigate } from 'react-router-dom';
import './ApplyJob.css';

const ApplyJob = () => {
  const { jobId } = useParams();
  const navigate = useNavigate();
  const [formData, setFormData] = useState({
    coverLetter: '',
    resume: null
  });
  const [job, setJob] = useState(null);
  const [loading, setLoading] = useState({
    job: true,
    submitting: false
  });
  const [messages, setMessages] = useState({
    error: '',
    success: ''
  });

  useEffect(() => {
    const fetchJob = async () => {
      try {
        const token = localStorage.getItem('token');
        if (!token) {
          navigate('/login');
          return;
        }

        const response = await axios.get(
          `http://localhost:5000/api/jobs/${jobId}`,
          {
            headers: {
              Authorization: `Bearer ${token}`,
              'Cache-Control': 'no-cache'
            }
          }
        );

        if (response.data.success) {
          setJob(response.data.job);
        } else {
          setMessages(prev => ({
            ...prev,
            error: response.data.message || 'Job details not available'
          }));
        }
      } catch (error) {
        console.error('Fetch job error:', error);
        let errorMessage = 'Failed to load job details';
        
        if (error.response) {
          if (error.response.status === 401) {
            errorMessage = 'Please login to apply for jobs';
            navigate('/login');
          } else if (error.response.status === 404) {
            errorMessage = 'Job not found or no longer available';
          }
        }

        setMessages(prev => ({ ...prev, error: errorMessage }));
      } finally {
        setLoading(prev => ({ ...prev, job: false }));
      }
    };

    fetchJob();
  }, [jobId, navigate]);

  const handleChange = (e) => {
    const { name, value, files } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: files ? files[0] : value
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(prev => ({ ...prev, submitting: true }));
    setMessages(prev => ({ ...prev, error: '' }));

    if (!formData.resume) {
      setMessages(prev => ({
        ...prev,
        error: 'Please upload your resume'
      }));
      setLoading(prev => ({ ...prev, submitting: false }));
      return;
    }

    try {
      const token = localStorage.getItem('token');
      if (!token) {
        navigate('/login');
        return;
      }

      const formPayload = new FormData();
      formPayload.append('jobId', jobId);
      formPayload.append('coverLetter', formData.coverLetter);
      formPayload.append('resume', formData.resume);

      const response = await axios.post(
        'http://localhost:5000/api/applications',
        formPayload,
        {
          headers: {
            Authorization: `Bearer ${token}`,
            'Content-Type': 'multipart/form-data'
          }
        }
      );

      if (response.data.success) {
        setMessages(prev => ({
          ...prev,
          success: 'Application submitted successfully!'
        }));
        setTimeout(() => navigate('/dashboard'), 2000);
      }
    } catch (error) {
      console.error('Application error:', error);
      let errorMessage = 'Error submitting application';
      
      if (error.response) {
        if (error.response.status === 401) {
          errorMessage = 'Session expired. Please login again.';
          navigate('/login');
        } else {
          errorMessage = error.response.data.message || errorMessage;
        }
      }

      setMessages(prev => ({ ...prev, error: errorMessage }));
    } finally {
      setLoading(prev => ({ ...prev, submitting: false }));
    }
  };

  if (loading.job) {
    return (
      <div className="loading-container">
        <div className="spinner"></div>
        <p>Loading job details...</p>
      </div>
    );
  }

  return (
    <div className="apply-job-container">
      <div className="header-section">
        <h2>Apply for {job?.title || 'this position'}</h2>
        <button 
          onClick={() => navigate(-1)} 
          className="back-button"
        >
          &larr; Back to Job
        </button>
      </div>

      {messages.error && (
        <div className="alert error">
          <p>{messages.error}</p>
        </div>
      )}

      {messages.success && (
        <div className="alert success">
          <p>{messages.success}</p>
        </div>
      )}

      {job && (
        <div className="job-details-card">
          <h3>{job.company}</h3>
          <div className="details-grid">
            <div>
              <span className="detail-label">Location:</span>
              <span>{job.location}</span>
            </div>
            <div>
              <span className="detail-label">Salary:</span>
              <span>{job.salary}</span>
            </div>
            <div>
              <span className="detail-label">Type:</span>
              <span>{job.jobType}</span>
            </div>
          </div>
          <p className="job-description">{job.description}</p>
        </div>
      )}

      <form onSubmit={handleSubmit} className="application-form">
        <div className="form-group">
          <label htmlFor="coverLetter">Cover Letter *</label>
          <textarea
            id="coverLetter"
            name="coverLetter"
            value={formData.coverLetter}
            onChange={handleChange}
            required
            placeholder="Explain why you're a good fit for this position..."
          />
        </div>

        <div className="form-group">
          <label htmlFor="resume">Resume *</label>
          <div className="file-upload-wrapper">
            <input
              type="file"
              id="resume"
              name="resume"
              accept=".pdf,.doc,.docx"
              onChange={handleChange}
              required
            />
            <label htmlFor="resume" className="file-upload-label">
              {formData.resume ? formData.resume.name : 'Choose file'}
            </label>
            {formData.resume && (
              <button 
                type="button" 
                onClick={() => setFormData(prev => ({ ...prev, resume: null }))}
                className="clear-file"
              >
                Ã—
              </button>
            )}
          </div>
          {formData.resume && (
            <p className="file-info">
              {Math.round(formData.resume.size / 1024)} KB - {
                formData.resume.type === 'application/pdf' ? 'PDF' : 'Word Document'
              }
            </p>
          )}
        </div>

        <button 
          type="submit" 
          disabled={loading.submitting}
          className="submit-button"
        >
          {loading.submitting ? (
            <>
              <span className="spinner"></span> Processing...
            </>
          ) : (
            'Submit Application'
          )}
        </button>
      </form>
    </div>
  );
};

export default ApplyJob;
