import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Link,  } from 'react-router-dom';

const Dashboard = ({ role }) => {
  const [appliedJobs, setAppliedJobs] = useState([]);
  const [companyApplications, setCompanyApplications] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    const fetchData = async () => {
      try {
        const token = localStorage.getItem('token');
        if (!token) return;

        setLoading(true);
        
        if (role === 'jobSeeker') {
          // Fetch jobs applied by the job seeker
          const response = await axios.get('http://localhost:5000/api/applications/my-applications', {
            headers: { Authorization: `Bearer ${token}` }
          });
          setAppliedJobs(response.data.applications);
        } 
        else if (role === 'company') {
          // Fetch applications for company's jobs
          const response = await axios.get('http://localhost:5000/api/applications/company', {
            headers: { Authorization: `Bearer ${token}` }
          });
          setCompanyApplications(response.data.applications);
        }
      } catch (err) {
        setError(err.response?.data?.message || 'Failed to fetch data');
      } finally {
        setLoading(false);
      }
    };

    fetchData();
  }, [role]);

  if (loading) return <div className="loading">Loading...</div>;
  if (error) return <div className="error">{error}</div>;

  return (
    <div className="dashboard">
      <h2>This is dashboard</h2> {/* Displaying the "This is dashboard" message */}

      {role === 'company' ? (
        <div className="company-view">
          <h3>Job Applications</h3>
          {companyApplications.length > 0 ? (
            <div className="applications-list">
              {companyApplications.map(application => (
                <div key={application._id} className="application-card">
                  <h4>Application for: {application.job.title}</h4>
                  <p>Applicant: {application.user.name} ({application.user.email})</p>
                  <p>Status: <span className={`status-${application.status}`}>{application.status}</span></p>
                  <p>Cover Letter: {application.coverLetter}</p>
                  <a 
                    href={`http://localhost:5000${application.resume}`} 
                    target="_blank" 
                    rel="noopener noreferrer"
                    className="resume-link"
                  >
                    View Resume
                  </a>
                </div>
              ))}
            </div>
          ) : (
            <p>No applications received yet.</p>
          )}
        </div>
      ) : role === 'jobSeeker' ? (
        <div className="jobseeker-view">
          <h3>Your Job Applications</h3>
          {appliedJobs.length > 0 ? (
            <div className="applications-list">
              {appliedJobs.map(application => (
                <div key={application._id} className="application-card">
                  <h4>{application.job.title}</h4>
                  <p>Company: {application.job.company}</p>
                  <p>Location: {application.job.location}</p>
                  <p>Status: <span className={`status-${application.status}`}>{application.status}</span></p>
                  <p>Applied on: {new Date(application.createdAt).toLocaleDateString()}</p>
                  <a 
                    href={`http://localhost:5000${application.resume}`} 
                    target="_blank" 
                    rel="noopener noreferrer"
                    className="resume-link"
                  >
                    View Your Resume
                  </a>
                </div>
              ))}
            </div>
          ) : (
            <div>
              <p>You haven't applied to any jobs yet.</p>
              <Link to="/" className="browse-jobs-link">
                Browse available jobs
              </Link>
            </div>
          )}
        </div>
      ) : (
        <div>Please log in to see your dashboard.</div>
      )}
    </div>
  );
};

export default Dashboard;
