import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useParams, useNavigate } from 'react-router-dom';
import './ApplyJob.css'; // Make sure to create this for styling

const ApplyJob = () => {
  const { jobId } = useParams();
  const navigate = useNavigate();
  const [coverLetter, setCoverLetter] = useState('');
  const [resume, setResume] = useState(null);
  const [job, setJob] = useState(null);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState(false);
  const [isLoading, setIsLoading] = useState({
    jobLoading: true,
    submitting: false
  });

  useEffect(() => {
    const fetchJob = async () => {
      try {
        const token = localStorage.getItem('token');
        if (!token) {
          navigate('/login');
          return;
        }

        const response = await axios.get(
          `http://localhost:5000/api/jobs/${jobId}`, 
          {
            headers: { 
              Authorization: `Bearer ${token}`,
              'Cache-Control': 'no-cache'
            }
          }
        );

        if (response.data.success) {
          setJob(response.data.job);
        } else {
          setError('Job not found or no longer available');
        }
      } catch (error) {
        console.error('Error fetching job:', error);
        if (error.response?.status === 404) {
          setError('Job not found or no longer available');
        } else if (error.response?.status === 401) {
          setError('Please login to apply for jobs');
          navigate('/login');
        } else {
          setError('Failed to load job details. Please try again later.');
        }
      } finally {
        setIsLoading(prev => ({ ...prev, jobLoading: false }));
      }
    };

    fetchJob();
  }, [jobId, navigate]);

  const handleFileChange = (e) => {
    const file = e.target.files[0];
    if (file && ['application/pdf', 'application/msword', 'application/vnd.openxmlformats-officedocument.wordprocessingml.document'].includes(file.type)) {
      setResume(file);
      setError('');
    } else {
      setError('Please upload a valid PDF or Word document');
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setIsLoading(prev => ({ ...prev, submitting: true }));
    setError('');
    
    if (!resume) {
      setError('Please upload your resume');
      setIsLoading(prev => ({ ...prev, submitting: false }));
      return;
    }

    try {
      const token = localStorage.getItem('token');
      if (!token) {
        navigate('/login');
        return;
      }

      const formData = new FormData();
      formData.append('jobId', jobId);
      formData.append('coverLetter', coverLetter);
      formData.append('resume', resume);

      const response = await axios.post(
        'http://localhost:5000/api/applications', 
        formData, 
        {
          headers: {
            Authorization: `Bearer ${token}`,
            'Content-Type': 'multipart/form-data'
          }
        }
      );

      if (response.data.success) {
        setSuccess(true);
        setTimeout(() => navigate('/dashboard'), 2000);
      }
    } catch (error) {
      console.error('Application error:', error);
      if (error.response?.status === 401) {
        setError('Session expired. Please login again.');
        navigate('/login');
      } else {
        setError(error.response?.data?.message || 'Error submitting application. Please try again.');
      }
    } finally {
      setIsLoading(prev => ({ ...prev, submitting: false }));
    }
  };

  if (isLoading.jobLoading) {
    return <div className="loading">Loading job details...</div>;
  }

  return (
    <div className="apply-job-container">
      <h2>Apply for {job?.title || 'this job'}</h2>
      
      {error && <div className="error-message">{error}</div>}
      {success && (
        <div className="success-message">
          Application submitted successfully! Redirecting...
        </div>
      )}
      
      {job ? (
        <div className="job-info">
          <h3>{job.company}</h3>
          <p>Location: {job.location}</p>
          <p>Salary: {job.salary}</p>
          <p>Job Type: {job.jobType}</p>
        </div>
      ) : (
        !error && <div className="info-message">Job details not available</div>
      )}
      
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label htmlFor="coverLetter">Cover Letter:</label>
          <textarea
            id="coverLetter"
            value={coverLetter}
            onChange={(e) => setCoverLetter(e.target.value)}
            required
            placeholder="Explain why you're a good fit for this position..."
            className={error.includes('cover letter') ? 'error-field' : ''}
          />
        </div>
        
        <div className="form-group">
          <label htmlFor="resume">Upload Resume (PDF or Word):</label>
          <input
            type="file"
            id="resume"
            accept=".pdf,.doc,.docx"
            onChange={handleFileChange}
            required
            className={error.includes('resume') ? 'error-field' : ''}
          />
          {resume && (
            <p className="file-info">
              Selected file: {resume.name} ({Math.round(resume.size / 1024)} KB)
            </p>
          )}
        </div>
        
        <button 
          type="submit" 
          disabled={isLoading.submitting}
          className="submit-btn"
        >
          {isLoading.submitting ? (
            <>
              <span className="spinner"></span> Submitting...
            </>
          ) : (
            'Submit Application'
          )}
        </button>
      </form>
    </div>
  );
};

export default ApplyJob;
