import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Link, useNavigate } from 'react-router-dom';
import './Dashboard.css';

const Dashboard = ({ role }) => {
  const [appliedJobs, setAppliedJobs] = useState([]);
  const [companyApplications, setCompanyApplications] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const navigate = useNavigate();

  useEffect(() => {
    const fetchData = async () => {
      try {
        const token = localStorage.getItem('token');
        if (!token) {
          navigate('/login');
          return;
        }

        setLoading(true);
        
        if (role === 'jobSeeker') {
          // Redirect job seekers to home page but still fetch their applications
          navigate('/');
          const response = await axios.get('http://localhost:5000/api/applications/my-applications', {
            headers: { Authorization: `Bearer ${token}` }
          });
          setAppliedJobs(response.data.applications);
        } 
        else if (role === 'company') {
          const response = await axios.get('http://localhost:5000/api/applications/company', {
            headers: { Authorization: `Bearer ${token}` }
          });
          setCompanyApplications(response.data.applications);
        }
      } catch (err) {
        setError(err.response?.data?.message || 'Failed to fetch data');
      } finally {
        setLoading(false);
      }
    };

    fetchData();
  }, [role, navigate]);

  if (loading) return <div className="loading">Loading...</div>;
  if (error) return <div className="error">{error}</div>;

  // For job seekers, we'll show applied jobs on the home page
  if (role === 'jobSeeker') {
    return null;
  }

  return (
    <div className="dashboard-container">
      <h2>Company Dashboard</h2>
      
      <div className="dashboard-header">
        <h3>Applications Received</h3>
        <Link to="/post-job" className="post-job-button">
          Post New Job
        </Link>
      </div>

      {companyApplications.length > 0 ? (
        <div className="applications-grid">
          {companyApplications.map(application => (
            <div key={application._id} className="application-card">
              <div className="application-header">
                <h3>{application.job.title}</h3>
                <span className={`status-badge status-${application.status}`}>
                  {application.status}
                </span>
              </div>
              
              <div className="applicant-info">
                <h4>Applicant Details</h4>
                <p><strong>Name:</strong> {application.user.name}</p>
                <p><strong>Email:</strong> {application.user.email}</p>
              </div>
              
              <div className="application-content">
                <h4>Cover Letter</h4>
                <p className="cover-letter">{application.coverLetter}</p>
                
                <div className="application-actions">
                  <a 
                    href={`http://localhost:5000${application.resume}`} 
                    target="_blank" 
                    rel="noopener noreferrer"
                    className="action-button"
                  >
                    View Resume
                  </a>
                  <Link 
                    to={`/job/${application.job._id}`} 
                    className="action-button view-job"
                  >
                    View Job
                  </Link>
                </div>
              </div>
              
              <div className="application-footer">
                <p>Applied on: {new Date(application.createdAt).toLocaleDateString()}</p>
              </div>
            </div>
          ))}
        </div>
      ) : (
        <div className="no-applications">
          <p>No applications received yet.</p>
          <Link to="/post-job" className="post-job-button">
            Post Your First Job
          </Link>
        </div>
      )}
    </div>
  );
};

export default Dashboard;
