import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import './NavBar.css';

function NavBar() {
  const [isMobile, setIsMobile] = useState(false); // To toggle menu visibility on mobile

  // Toggle the mobile menu
  const toggleMenu = () => {
    setIsMobile(!isMobile);
  };

  return (
    <nav className="navbar">
      <div className="navbar-brand">
        <h2>Job Board</h2>
      </div>
      <div className={`navbar-links ${isMobile ? 'active' : ''}`}>
        <Link to="/" className="navbar-link">Home</Link>
        <Link to="/post-job" className="navbar-link">Post a Job</Link>
      </div>
      {/* Hamburger icon */}
      <button className="navbar-hamburger" onClick={toggleMenu}>
        <span className="navbar-hamburger-line"></span>
        <span className="navbar-hamburger-line"></span>
        <span className="navbar-hamburger-line"></span>
      </button>
    </nav>
  );
}

export default NavBar;
