import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Link, useNavigate } from 'react-router-dom';
import './Dashboard.css'; // Make sure to create this CSS file

const Dashboard = ({ role }) => {
  const [appliedJobs, setAppliedJobs] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const navigate = useNavigate();

  useEffect(() => {
    // Redirect job seekers to home page when dashboard loads
    if (role === 'jobSeeker') {
      navigate('/');
      return;
    }

    const fetchAppliedJobs = async () => {
      try {
        const token = localStorage.getItem('token');
        if (!token) return;

        const response = await axios.get('http://localhost:5000/api/applications/my-applications', {
          headers: { Authorization: `Bearer ${token}` }
        });
        
        setAppliedJobs(response.data.applications);
        setLoading(false);
      } catch (err) {
        setError(err.response?.data?.message || 'Failed to fetch your applications');
        setLoading(false);
      }
    };

    fetchAppliedJobs();
  }, [role, navigate]);

  // For job seekers, we'll show applied jobs on the homepage instead
  // This component will primarily handle company dashboard

  if (role !== 'company') {
    return null; // Job seekers are redirected to home page
  }

  if (loading) return <div className="loading">Loading your applications...</div>;
  if (error) return <div className="error">{error}</div>;

  return (
    <div className="dashboard-container">
      <h2>Your Posted Jobs</h2>
      
      {appliedJobs.length > 0 ? (
        <div className="applications-grid">
          {appliedJobs.map(application => (
            <div key={application._id} className="application-card">
              <div className="job-info">
                <h3>{application.job.title}</h3>
                <p className="company">{application.job.company}</p>
                <p className="location">{application.job.location}</p>
              </div>
              
              <div className="application-details">
                <p className="status">
                  Status: <span className={`status-${application.status}`}>
                    {application.status}
                  </span>
                </p>
                <p className="applied-date">
                  Applied on: {new Date(application.createdAt).toLocaleDateString()}
                </p>
                <a 
                  href={`http://localhost:5000${application.resume}`} 
                  target="_blank" 
                  rel="noopener noreferrer"
                  className="resume-link"
                >
                  View Resume
                </a>
              </div>
            </div>
          ))}
        </div>
      ) : (
        <div className="no-applications">
          <p>No applications received yet for your posted jobs.</p>
          <Link to="/post-job" className="post-job-link">
            Post a new job
          </Link>
        </div>
      )}
    </div>
  );
};

export default Dashboard;
