import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import './Dashboard.css';

const Dashboard = ({ role }) => {
  const [userData, setUserData] = useState(null);
  const [applications, setApplications] = useState([]);
  const [postedJobs, setPostedJobs] = useState([]);
  const [jobSeekers, setJobSeekers] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const navigate = useNavigate();

  useEffect(() => {
    const fetchData = async () => {
      try {
        const token = localStorage.getItem('token');
        if (!token) {
          navigate('/login');
          return;
        }

        // Fetch user data
        const userResponse = await axios.get('http://localhost:5000/api/auth/me', {
          headers: { Authorization: `Bearer ${token}` }
        });
        setUserData(userResponse.data.user);

        if (role === 'jobSeeker') {
          // Fetch job seeker's applications
          const appsResponse = await axios.get('http://localhost:5000/api/applications/my', {
            headers: { Authorization: `Bearer ${token}` }
          });
          setApplications(appsResponse.data?.applications || []);
        } else if (role === 'company') {
          // Fetch company's posted jobs
          const jobsResponse = await axios.get('http://localhost:5000/api/jobs/my', {
            headers: { Authorization: `Bearer ${token}` }
          });
          setPostedJobs(jobsResponse.data?.jobs || []);

          // Fetch job seekers who applied to company's jobs
          const seekersResponse = await axios.get('http://localhost:5000/api/applications/company', {
            headers: { Authorization: `Bearer ${token}` }
          });
          setJobSeekers(seekersResponse.data?.applications || []);
        }
      } catch (error) {
        console.error('Dashboard error:', error);
        setError('Failed to load dashboard data. Please try again.');
        if (error.response?.status === 401) {
          localStorage.removeItem('token');
          localStorage.removeItem('role');
          navigate('/login');
        }
      } finally {
        setLoading(false);
      }
    };

    fetchData();
  }, [role, navigate]);

  // Helper function to safely format status
  const formatStatus = (status) => {
    if (!status) return 'Pending';
    return status.charAt(0).toUpperCase() + status.slice(1);
  };

  if (loading) return <div className="loading">Loading dashboard...</div>;
  if (error) return <div className="error">{error}</div>;

  return (
    <div className="dashboard-container">
      <h2>Welcome, {userData?.name || 'User'}</h2>
      
      {role === 'jobSeeker' ? (
        <div className="job-seeker-dashboard">
          <h3>Your Job Applications</h3>
          {applications.length > 0 ? (
            <div className="applications-list">
              {applications.map(app => (
                <div key={app._id} className="application-card">
                  <div className="application-header">
                    <h4>{app.job?.title || 'Job Title Not Available'}</h4>
                    <span className={`status-badge status-${app.status || 'pending'}`}>
                      {formatStatus(app.status)}
                    </span>
                  </div>
                  <p className="company-info">
                    <strong>Company:</strong> {app.job?.company || 'N/A'}
                  </p>
                  <p className="location-info">
                    <strong>Location:</strong> {app.job?.location || 'N/A'}
                  </p>
                  <p className="salary-info">
                    <strong>Salary:</strong> {app.job?.salary || 'Not specified'}
                  </p>
                  <p className="applied-date">
                    Applied on: {app.createdAt ? new Date(app.createdAt).toLocaleDateString() : 'N/A'}
                  </p>
                </div>
              ))}
            </div>
          ) : (
            <div className="no-applications">
              <p>You haven't applied to any jobs yet.</p>
              <button 
                onClick={() => navigate('/')}
                className="browse-jobs-btn"
              >
                Browse Available Jobs
              </button>
            </div>
          )}
        </div>
      ) : (
        <div className="company-dashboard">
          <div className="dashboard-section">
            <h3>Your Posted Jobs</h3>
            {postedJobs.length > 0 ? (
              <div className="jobs-list">
                {postedJobs.map(job => (
                  <div key={job._id} className="job-card">
                    <h4>{job.title || 'Untitled Job'}</h4>
                    <p><strong>Location:</strong> {job.location || 'N/A'}</p>
                    <p><strong>Salary:</strong> {job.salary || 'Not specified'}</p>
                    <p><strong>Applications:</strong> {job.applicationCount || 0}</p>
                    <button 
                      onClick={() => navigate(`/job-applications/${job._id}`)}
                      className="view-applications-btn"
                    >
                      Manage Applications
                    </button>
                  </div>
                ))}
              </div>
            ) : (
              <div className="no-jobs">
                <p>You haven't posted any jobs yet.</p>
              </div>
            )}
            <button 
              onClick={() => navigate('/post-job')}
              className="post-job-btn"
            >
              Post New Job
            </button>
          </div>

          <div className="dashboard-section">
            <h3>Recent Applicants</h3>
            {jobSeekers.length > 0 ? (
              <div className="applicants-list">
                {jobSeekers.slice(0, 5).map(application => (
                  <div key={application._id} className="applicant-card">
                    <div className="applicant-info">
                      <h4>{application.user?.name || 'Anonymous'}</h4>
                      <p>{application.user?.email || 'No email provided'}</p>
                      <p>
                        Applied for: <strong>{application.job?.title || 'Unknown Job'}</strong>
                      </p>
                      <p>
                        Status: <span className={`status-${application.status || 'pending'}`}>
                          {formatStatus(application.status)}
                        </span>
                      </p>
                    </div>
                    <div className="applicant-actions">
                      <button 
                        onClick={() => navigate(`/job-applications/${application.job?._id}`)}
                        className="view-details-btn"
                      >
                        View Details
                      </button>
                    </div>
                  </div>
                ))}
                {jobSeekers.length > 5 && (
                  <button 
                    onClick={() => navigate('/job-applications')}
                    className="view-all-btn"
                  >
                    View All Applicants
                  </button>
                )}
              </div>
            ) : (
              <div className="no-applicants">
                <p>No applicants yet for your jobs.</p>
              </div>
            )}
          </div>
        </div>
      )}
    </div>
  );
};

export default Dashboard;
