import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';
import './Dashboard.css'; // Make sure the CSS file exists

const Dashboard = ({ role }) => {
  const [appliedJobs, setAppliedJobs] = useState([]);
  const [companyApplications, setCompanyApplications] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    const fetchData = async () => {
      try {
        const token = localStorage.getItem('token');
        if (!token) return;

        setLoading(true);

        // Fetch applied jobs for job seekers
        if (role === 'jobSeeker') {
          const response = await axios.get('http://localhost:5000/api/applications/my-applications', {
            headers: { Authorization: `Bearer ${token}` }
          });
          setAppliedJobs(response.data.applications);
        } 

        // Fetch applications for company's posted jobs
        else if (role === 'company') {
          const response = await axios.get('http://localhost:5000/api/applications/company', {
            headers: { Authorization: `Bearer ${token}` }
          });
          setCompanyApplications(response.data.applications);
        }
      } catch (err) {
        setError(err.response?.data?.message || 'Failed to fetch data');
      } finally {
        setLoading(false);
      }
    };

    fetchData();
  }, [role]);

  // Loading and error state display
  if (loading) return <div className="loading">Loading...</div>;
  if (error) return <div className="error">{error}</div>;

  // For Company Dashboard
  if (role === 'company') {
    return (
      <div className="dashboard">
        <h2>Company Dashboard</h2>
        <div className="company-view">
          <h3>Applications Received</h3>
          {companyApplications.length > 0 ? (
            <div className="applications-grid">
              {companyApplications.map(application => (
                <div key={application._id} className="application-card">
                  <div className="application-header">
                    <h4>{application.job.title}</h4>
                    <span className={`status-badge status-${application.status}`}>
                      {application.status}
                    </span>
                  </div>
                  <div className="applicant-info">
                    <p><strong>Applicant:</strong> {application.user.name}</p>
                    <p><strong>Email:</strong> {application.user.email}</p>
                  </div>
                  <div className="application-content">
                    <p><strong>Cover Letter:</strong></p>
                    <p className="cover-letter">{application.coverLetter}</p>
                    <a 
                      href={`http://localhost:5000${application.resume}`} 
                      target="_blank" 
                      rel="noopener noreferrer"
                      className="resume-link"
                    >
                      View Resume
                    </a>
                  </div>
                </div>
              ))}
            </div>
          ) : (
            <div className="no-applications">
              <p>No applications received yet.</p>
              <Link to="/post-job" className="post-job-button">
                Post a Job
              </Link>
            </div>
          )}
        </div>
      </div>
    );
  }

  // For Job Seeker Dashboard (Updated)
  return (
    <div className="dashboard">
      <h2>Your Applied Jobs</h2>
      
      {appliedJobs.length > 0 ? (
        <div className="applied-jobs-container">
          {appliedJobs.map((application) => (
            <div key={application._id} className="applied-job-card">
              <div className="job-info">
                <h3>{application.job.title}</h3>
                <p className="company">{application.job.company}</p>
                <p className="posted-by">Posted by: {application.job.postedBy.name}</p>
                <p className="location">{application.job.location}</p>
                <p className="salary">{application.job.salary}</p>
                <p className="description">{application.job.description}</p>
              </div>
              <div className="application-info">
                <p className="applied-badge">âœ“ You applied to this job</p>
                <p className="status">
                  Status: <span className={`status-${application.status}`} >
                    {application.status}
                  </span>
                </p>
                <p className="applied-date">
                  Applied on: {new Date(application.createdAt).toLocaleDateString()}
                </p>
                <a 
                  href={`http://localhost:5000${application.resume}`} 
                  target="_blank" 
                  rel="noopener noreferrer"
                  className="view-resume"
                >
                  View your submitted resume
                </a>
              </div>
            </div>
          ))}
        </div>
      ) : (
        <div className="no-applications">
          <p>You haven't applied to any jobs yet.</p>
          <Link to="/" className="browse-jobs-link">
            Browse available jobs
          </Link>
        </div>
      )}
    </div>
  );
};

export default Dashboard;
