import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Route, Switch, Redirect } from 'react-router-dom';
import axios from 'axios';

// Import components
import Login from './components/Login';
import Signup from './components/Signup';
import JobList from './components/JobList';
import JobPostForm from './components/JobPostForm';
import Dashboard from './components/Dashboard';
import Navbar from './components/Navbar';

// App Component
const App = () => {
  const [authToken, setAuthToken] = useState(localStorage.getItem('token') || '');
  const [role, setRole] = useState(localStorage.getItem('role') || '');

  // Check if the user is logged in
  useEffect(() => {
    if (authToken) {
      // Set role from local storage
      setRole(localStorage.getItem('role'));
    }
  }, [authToken]);

  // Handle Logout
  const logout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('role');
    setAuthToken('');
    setRole('');
  };

  return (
    <Router>
      <div>
        <Navbar logout={logout} role={role} />
        <Switch>
          <Route path="/login">
            {authToken ? <Redirect to="/" /> : <Login setAuthToken={setAuthToken} setRole={setRole} />}
          </Route>
          <Route path="/signup">
            {authToken ? <Redirect to="/" /> : <Signup setAuthToken={setAuthToken} setRole={setRole} />}
          </Route>
          <Route path="/jobs" exact component={JobList} />
          <Route
            path="/post-job"
            render={() =>
              role === 'company' ? (
                <JobPostForm authToken={authToken} />
              ) : (
                <Redirect to="/" />
              )
            }
          />
          <Route path="/dashboard" render={() => (authToken ? <Dashboard role={role} /> : <Redirect to="/login" />)} />
          <Route path="/" exact component={JobList} />
        </Switch>
      </div>
    </Router>
  );
};

export default App;
