import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import './Dashboard.css';

const Dashboard = ({ role }) => {
  const [userData, setUserData] = useState(null);
  const [applications, setApplications] = useState([]);
  const [postedJobs, setPostedJobs] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const navigate = useNavigate();

  useEffect(() => {
    const fetchData = async () => {
      try {
        const token = localStorage.getItem('token');
        if (!token) {
          navigate('/login');
          return;
        }

        // Fetch user data
        const userResponse = await axios.get('http://localhost:5000/api/auth/me', {
          headers: { Authorization: `Bearer ${token}` }
        });
        setUserData(userResponse.data.user);

        // Fetch role-specific data
        if (role === 'jobSeeker') {
          const appsResponse = await axios.get('http://localhost:5000/api/applications/my', {
            headers: { Authorization: `Bearer ${token}` }
          });
          setApplications(appsResponse.data.applications || []);
        } else if (role === 'company') {
          const [jobsResponse, appsCountResponse] = await Promise.all([
            axios.get('http://localhost:5000/api/jobs/my', {
              headers: { Authorization: `Bearer ${token}` }
            }),
            axios.get('http://localhost:5000/api/applications/counts', {
              headers: { Authorization: `Bearer ${token}` }
            })
          ]);
          
          const jobsWithCounts = jobsResponse.data.jobs.map(job => {
            const countData = appsCountResponse.data.counts.find(c => c._id === job._id);
            return {
              ...job,
              applicationCount: countData ? countData.count : 0
            };
          });
          
          setPostedJobs(jobsWithCounts || []);
        }

        setLoading(false);
      } catch (error) {
        console.error('Dashboard error:', error);
        setError('Failed to load dashboard data. Please try again.');
        setLoading(false);
      }
    };

    fetchData();
  }, [role, navigate]);

  if (loading) return <div className="loading">Loading dashboard...</div>;
  if (error) return <div className="error">{error}</div>;

  return (
    <div className="dashboard-container">
      <h2>Welcome, {userData?.name}</h2>
      
      {role === 'jobSeeker' ? (
        <div className="job-seeker-dashboard">
          <h3>Your Job Applications</h3>
          {applications.length > 0 ? (
            <div className="applications-list">
              {applications.map(app => (
                <div key={app._id} className="application-card">
                  <h4>{app.job?.title || 'Job Title Not Available'}</h4>
                  <p>Company: {app.job?.company || 'N/A'}</p>
                  <p>Status: <span className={`status-${app.status}`}>
                    {app.status.charAt(0).toUpperCase() + app.status.slice(1)}
                  </span></p>
                  <p>Applied on: {new Date(app.createdAt).toLocaleDateString()}</p>
                  <div className="application-actions">
                    <button 
                      onClick={() => navigate(`/job/${app.job?._id}`)}
                      className="view-job-btn"
                    >
                      View Job
                    </button>
                  </div>
                </div>
              ))}
            </div>
          ) : (
            <div className="no-applications">
              <p>You haven't applied to any jobs yet.</p>
              <button 
                onClick={() => navigate('/')}
                className="browse-jobs-btn"
              >
                Browse Available Jobs
              </button>
            </div>
          )}
        </div>
      ) : (
        <div className="company-dashboard">
          <h3>Your Posted Jobs</h3>
          {postedJobs.length > 0 ? (
            <div className="jobs-list">
              {postedJobs.map(job => (
                <div key={job._id} className="job-card">
                  <h4>{job.title}</h4>
                  <p>{job.company} â€¢ {job.location}</p>
                  <p>Salary: {job.salary}</p>
                  <div className="job-actions">
                    <button 
                      onClick={() => navigate(`/job-applications/${job._id}`)}
                      className="view-applications-btn"
                    >
                      View Applications ({job.applicationCount || 0})
                    </button>
                    <button 
                      onClick={() => navigate(`/job/${job._id}/edit`)}
                      className="edit-job-btn"
                    >
                      Edit
                    </button>
                  </div>
                </div>
              ))}
            </div>
          ) : (
            <div className="no-jobs">
              <p>You haven't posted any jobs yet.</p>
            </div>
          )}
          <button 
            onClick={() => navigate('/post-job')}
            className="post-job-btn"
          >
            Post New Job
          </button>
        </div>
      )}
    </div>
  );
};

export default Dashboard;
