import React, { useState, useEffect } from 'react';
import ApplyForm from './ApplyForm';  // Import the ApplyForm component

function App() {
  const [jobs, setJobs] = useState([]);

  useEffect(() => {
    fetch('http://localhost:5000/api/jobs')
      .then(res => res.json())
      .then(data => setJobs(data));
  }, []);

  return (
    <div className="App">
      <h1>Job Board</h1>
      <ul>
        {jobs.map(job => (
          <li key={job._id}>
            <h2>{job.title}</h2>
            <p>{job.company}</p>
            <p>{job.category}</p>
            <p>{job.location}</p>
            <ApplyForm jobId={job._id} />  {/* Include the ApplyForm component */}
          </li>
        ))}
      </ul>
    </div>
  );
}

export default App;
