import React, { useState } from 'react';
import axios from 'axios';

const Signup = ({ setAuthToken, setRole }) => {
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [role, setRoleSelect] = useState('jobSeeker');
  const [error, setError] = useState('');

  const handleSignup = async (e) => {
    e.preventDefault();

    try {
      // Send signup request to backend
      const response = await axios.post('http://localhost:5000/api/auth/signup', { 
        name, email, password, role 
      });

      // Log the response for debugging purposes
      console.log(response);

      // If signup is successful, store the JWT token and role
      const { token, role: userRole } = response.data;

      // Save token and role to localStorage
      localStorage.setItem('token', token);
      localStorage.setItem('role', userRole);

      // Call setAuthToken and setRole (optional based on your app structure)
      setAuthToken(token);
      setRole(userRole);

      // Redirect user to another page (optional)
      window.location.href = '/';  // Redirect to homepage after successful signup

    } catch (error) {
      // Handle error
      console.error('Error during signup:', error);
      setError('Error during signup. Please try again.');
    }
  };

  return (
    <div>
      <h2>Signup</h2>
      {error && <p style={{ color: 'red' }}>{error}</p>}
      <form onSubmit={handleSignup}>
        <input
          type="text"
          placeholder="Name"
          value={name}
          onChange={(e) => setName(e.target.value)}
          required
        />
        <input
          type="email"
          placeholder="Email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          required
        />
        <input
          type="password"
          placeholder="Password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          required
        />
        <select onChange={(e) => setRoleSelect(e.target.value)} value={role}>
          <option value="jobSeeker">Job Seeker</option>
          <option value="company">Company</option>
        </select>
        <button type="submit">Signup</button>
      </form>
    </div>
  );
};

export default Signup;
