import React, { useState, useEffect } from 'react';

function App() {
  const [category, setCategory] = useState('');
  const [jobs, setJobs] = useState([]);  // Initialize jobs as an empty array

  useEffect(() => {
    const url = category ? `http://localhost:5000/api/jobs?category=${category}` : 'http://localhost:5000/api/jobs';
    fetch(url)
      .then((res) => res.json())
      .then((data) => {
        // Ensure the response data is an array before setting it
        if (Array.isArray(data)) {
          setJobs(data);
        } else {
          console.error('Received data is not an array', data);
        }
      })
      .catch((err) => console.error('Error fetching jobs:', err));
  }, [category]);

  return (
    <div className="App">
      <h1>Job Board</h1>
      <select onChange={(e) => setCategory(e.target.value)}>
        <option value="">All Categories</option>
        <option value="IT">IT</option>
        <option value="Marketing">Marketing</option>
        {/* Add other categories here */}
      </select>
      <ul>
        {jobs.length === 0 ? (
          <li>No jobs available</li>
        ) : (
          jobs.map((job) => (
            <li key={job._id}>
              <h2>{job.title}</h2>
              <p>{job.company}</p>
              <p>{job.category}</p>
              <p>{job.location}</p>
            </li>
          ))
        )}
      </ul>
    </div>
  );
}

export default App;
