import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import './Dashboard.css'; // Create this file for styling

const Dashboard = ({ role }) => {
  const [userData, setUserData] = useState(null);
  const [applications, setApplications] = useState([]);
  const [postedJobs, setPostedJobs] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const navigate = useNavigate();

  useEffect(() => {
    const fetchData = async () => {
      try {
        const token = localStorage.getItem('token');
        if (!token) {
          navigate('/login');
          return;
        }

        // Fetch user data
        const userResponse = await axios.get('http://localhost:5000/api/auth/me', {
          headers: { Authorization: `Bearer ${token}` }
        });
        setUserData(userResponse.data.user);

        // Role-specific data fetching
        if (role === 'jobSeeker') {
          const appsResponse = await axios.get('http://localhost:5000/api/applications/my', {
            headers: { Authorization: `Bearer ${token}` }
          });
          setApplications(appsResponse.data.applications);
        } else if (role === 'company') {
          const jobsResponse = await axios.get('http://localhost:5000/api/jobs/my', {
            headers: { Authorization: `Bearer ${token}` }
          });
          setPostedJobs(jobsResponse.data.jobs);
        }

        setLoading(false);
      } catch (error) {
        setError('Failed to load dashboard data');
        setLoading(false);
        console.error('Dashboard error:', error);
      }
    };

    fetchData();
  }, [role, navigate]);

  if (loading) return <div className="loading">Loading dashboard...</div>;
  if (error) return <div className="error">{error}</div>;

  return (
    <div className="dashboard-container">
      <h2>Welcome, {userData?.name}</h2>
      
      {role === 'jobSeeker' ? (
        <div className="job-seeker-dashboard">
          <h3>Your Applications</h3>
          {applications.length > 0 ? (
            <div className="applications-list">
              {applications.map(app => (
                <div key={app._id} className="application-card">
                  <h4>{app.job.title}</h4>
                  <p>Status: {app.status}</p>
                  <p>Applied on: {new Date(app.createdAt).toLocaleDateString()}</p>
                </div>
              ))}
            </div>
          ) : (
            <p>You haven't applied to any jobs yet.</p>
          )}
        </div>
      ) : (
        <div className="company-dashboard">
          <h3>Your Posted Jobs</h3>
          {postedJobs.length > 0 ? (
            <div className="jobs-list">
              {postedJobs.map(job => (
                <div key={job._id} className="job-card">
                  <h4>{job.title}</h4>
                  <p>{job.location} â€¢ {job.salary}</p>
                  <button onClick={() => navigate(`/job-applications/${job._id}`)}>
                    View Applications
                  </button>
                </div>
              ))}
            </div>
          ) : (
            <p>You haven't posted any jobs yet.</p>
          )}
          <button 
            onClick={() => navigate('/post-job')}
            className="post-job-btn"
          >
            Post New Job
          </button>
        </div>
      )}
    </div>
  );
};

export default Dashboard;
