import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';
import './Dashboard.css';

const Dashboard = ({ role }) => {
  const [appliedJobs, setAppliedJobs] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    const fetchAppliedJobs = async () => {
      try {
        const token = localStorage.getItem('token');
        if (!token) return;

        const response = await axios.get('http://localhost:5000/api/applications/my-applications', {
          headers: { Authorization: `Bearer ${token}` }
        });
        
        setAppliedJobs(response.data.applications);
      } catch (err) {
        setError(err.response?.data?.message || 'Failed to fetch your applications');
      } finally {
        setLoading(false);
      }
    };

    if (role === 'jobSeeker') {
      fetchAppliedJobs();
    }
  }, [role]);

  if (loading) return <div className="loading">Loading your applications...</div>;
  if (error) return <div className="error">{error}</div>;

  if (role === 'company') {
    // Company dashboard remains unchanged
    return (
      <div className="dashboard">
        <h2>Company Dashboard</h2>
        {/* ... existing company dashboard code ... */}
      </div>
    );
  }

  return (
    <div className="dashboard">
      <h2>Your Job Applications</h2>
      
      {appliedJobs.length > 0 ? (
        <div className="applied-jobs-container">
          {appliedJobs.map((application) => (
            <div key={application._id} className="applied-job-card">
              <div className="job-info">
                <h3>{application.job.title}</h3>
                <p className="company">{application.job.company}</p>
                <p className="location">{application.job.location}</p>
                <p className="salary">{application.job.salary}</p>
                <p className="description">{application.job.description}</p>
              </div>
              
              <div className="application-info">
                <p className="applied-badge">✓ You applied to this position</p>
                <p className="status">
                  Status: <span className={`status-${application.status}`}>
                    {application.status}
                  </span>
                </p>
                <p className="applied-date">
                  Applied on: {new Date(application.createdAt).toLocaleDateString()}
                </p>
                <a 
                  href={`http://localhost:5000${application.resume}`} 
                  target="_blank" 
                  rel="noopener noreferrer"
                  className="resume-link"
                >
                  View your submitted resume
                </a>
              </div>
            </div>
          ))}
        </div>
      ) : (
        <div className="no-applications">
          <p>You haven't applied to any jobs yet.</p>
          <Link to="/" className="browse-jobs-link">
            Browse available jobs
          </Link>
        </div>
      )}
    </div>
  );
};

export default Dashboard;
