import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useParams, useNavigate } from 'react-router-dom';
import './ApplyJob.css';

const ApplyJob = () => {
  const { jobId } = useParams();
  const navigate = useNavigate();
  const [coverLetter, setCoverLetter] = useState('');
  const [resume, setResume] = useState(null);
  const [job, setJob] = useState(null);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState(false);
  const [isLoading, setIsLoading] = useState(false);
  const [jobLoading, setJobLoading] = useState(true);

  useEffect(() => {
    const fetchJob = async () => {
      try {
        const token = localStorage.getItem('token');
        const response = await axios.get(`http://localhost:5000/api/jobs/${jobId}`, {
          headers: { Authorization: token ? `Bearer ${token}` : undefined }
        });
        
        if (response.data.success && response.data.job) {
          setJob(response.data.job);
        } else {
          setError('Job not found');
        }
      } catch (error) {
        console.error('Error fetching job:', error);
        setError(error.response?.data?.message || 'Failed to load job details');
      } finally {
        setJobLoading(false);
      }
    };

    fetchJob();
  }, [jobId]);

  const handleFileChange = (e) => {
    if (e.target.files && e.target.files[0]) {
      setResume(e.target.files[0]);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setIsLoading(true);
    setError('');
    
    if (!resume) {
      setError('Please upload your resume');
      setIsLoading(false);
      return;
    }

    try {
      const token = localStorage.getItem('token');
      if (!token) {
        throw new Error('Authentication required');
      }

      const formData = new FormData();
      formData.append('jobId', jobId);
      formData.append('coverLetter', coverLetter);
      formData.append('resume', resume);

      const response = await axios.post(
        'http://localhost:5000/api/applications', 
        formData, 
        {
          headers: {
            Authorization: `Bearer ${token}`,
            'Content-Type': 'multipart/form-data'
          }
        }
      );

      if (response.data.success) {
        setSuccess(true);
        setTimeout(() => navigate('/dashboard'), 2000);
      }
    } catch (error) {
      console.error('Application error:', error);
      setError(error.response?.data?.message || 'Error submitting application');
    } finally {
      setIsLoading(false);
    }
  };

  if (jobLoading) {
    return <div className="loading">Loading job details...</div>;
  }

  if (error && !job) {
    return (
      <div className="error-container">
        <div className="error-message">{error}</div>
        <button onClick={() => navigate('/')} className="back-button">
          Back to Job Listings
        </button>
      </div>
    );
  }

  return (
    <div className="apply-job-container">
      <h2>Apply for {job?.title || 'this position'}</h2>
      
      {job && (
        <div className="job-details">
          <h3>{job.company}</h3>
          <p><strong>Location:</strong> {job.location}</p>
          <p><strong>Salary:</strong> {job.salary}</p>
          <p><strong>Description:</strong> {job.description}</p>
        </div>
      )}
      
      {error && <div className="error-message">{error}</div>}
      {success && (
        <div className="success-message">
          Application submitted successfully! Redirecting...
        </div>
      )}
      
      <form onSubmit={handleSubmit} className="application-form">
        <div className="form-group">
          <label htmlFor="coverLetter">Cover Letter:</label>
          <textarea
            id="coverLetter"
            value={coverLetter}
            onChange={(e) => setCoverLetter(e.target.value)}
            required
            placeholder="Explain why you're a good fit for this position..."
            rows={6}
          />
        </div>
        
        <div className="form-group">
          <label htmlFor="resume">Upload Resume (PDF or DOC/DOCX):</label>
          <div className="file-upload-wrapper">
            <input
              type="file"
              id="resume"
              accept=".pdf,.doc,.docx"
              onChange={handleFileChange}
              required
            />
            <label htmlFor="resume" className="file-upload-label">
              {resume ? resume.name : 'Choose file...'}
            </label>
          </div>
          {resume && (
            <div className="file-info">
              <span>{resume.name}</span>
              <button 
                type="button" 
                onClick={() => setResume(null)}
                className="remove-file-btn"
              >
                Remove
              </button>
            </div>
          )}
        </div>
        
        <div className="form-actions">
          <button 
            type="button"
            onClick={() => navigate(-1)}
            className="cancel-btn"
          >
            Cancel
          </button>
          <button 
            type="submit" 
            disabled={isLoading}
            className="submit-btn"
          >
            {isLoading ? 'Submitting...' : 'Submit Application'}
          </button>
        </div>
      </form>
    </div>
  );
};

export default ApplyJob;
